import React, { Component } from "react";
import styles from './sideBarMenu.css';
// import Glyphicon from 'react-bootstrap/lib/Glyphicon';
import { Nav, NavItem, Navbar, NavDropdown, DropdownItem } from 'react-bootstrap';
import { NavLink } from 'react-router-dom';
import { Form, Button } from 'react-bootstrap';


class CreateRoomDiv extends Component {

    constructor(props){
        super(props);
        this.state = { Name: '', orgID: '' };
      }

      handleChange = event => {
        this.setState({ Name: event.target.value, orgID: event.target.value });
      };
      handleSubmit(event) {
        event.preventDefault();
        this.setState({ value: this.element.value });
      }

modalRef = React.createRef();

clickAddRoom = () => {
    console.log(this.modalRef)

    this.modalRef.current.setState({modalType: 'NewEventTable'}, () => {

      this.modalRef.current.toggleModal();
  });
    console.log(`you clicked the add event button`);
  };

  render() {
    return (
        <div className="mainDiv" >
            <div className="sideBarMenuContainer">
                <Navbar fluid="true" inverse="true" >
                    <Navbar.Collapse>
                        <Navbar.Text className="nt">
                            <NavLink className="nl" to="/Home">
                                Home
                        </NavLink>
                            <NavLink className="nl" to="/PatientList">
                                PatientList
                        </NavLink>
                            <NavLink className="nl" to="/CreateRoom">
                                CreateRoom
                        </NavLink>
                            <NavLink className="nl" to="/CompanyInfo">
                                Company Info
                        </NavLink>
                            <NavLink className="nl" to="/FrontDesk">
                                Front Desk
                        </NavLink>
                        </Navbar.Text>
                    </Navbar.Collapse>
                </Navbar>
            </div>
            <Form onSubmit={this.handleSubmit}>
            <label htmlFor="Name">Room Name</label>
         <input
           type="text"
           name="Name"
           value={this.state.Name}

         />
            <label htmlFor="orgID">Organization ID</label>
         <input
           type="text"
           name="orgID"
           value={this.state.orgID}

         />
  <Button variant="primary" type="submit"
  onClick={this.clickAddRoom}>
    Create Room
  </Button>
</Form>
<h3>Your room Name is: {this.state.Name}</h3>
        </div>

    )
};
}
//exportes our Nav component
export default CreateRoomDiv;
