import React, { Component } from "react";
import './style.css';
import config from '../../config';
// import Glyphicon from 'react-bootstrap/lib/Glyphicon';
import { Nav, NavItem, Navbar, NavDropdown, DropdownItem } from 'react-bootstrap';
import { NavLink } from 'react-router-dom';
import { Form, Button } from 'react-bootstrap';


class CreateRoomDiv extends Component {
  constructor() {
    super();
    this.state = {
      Name: '',
      orgID: ''
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  handleSubmit(event) {
    event.preventDefault();

    const data = new FormData(event.target);

    console.log(...data)


    console.log(this.state.Name)
    console.log(this.state.orgID)

    fetch("http://localhost:3010/api/room/createRoom", {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        Name: this.state.Name,
        orgID: this.state.orgID,

      })

    })

  }

  render() {
    return (
      <div className="mainDiv" >
        <div className="row">
          <div className="col-lg-4">
            <div className="sideBarMenuContainer">
              <Navbar fluid="true" inverse="true" >
                <Navbar.Collapse>
                  <Navbar.Text className="nt">
                    <NavLink className="nl" to="/PatientList">
                      Patient List
                        </NavLink>
                    <NavLink className="nl" to="/CreateRoom">
                      Create Room
                        </NavLink>
                    <NavLink className="nl" to="/CompanyInfo">
                      Company Info
                        </NavLink>
                    <NavLink className="nl" to="/FrontDesk">
                      Front Desk
                        </NavLink>
                  </Navbar.Text>
                </Navbar.Collapse>
              </Navbar>
            </div>
          </div>
          <Table className="patientTable" striped bordered hover>
            <thead>
              <tr>
                <th style={{ width: "20px" }}>Notes</th>
                <th>Room Number</th>
                <th>Room Name</th>
                <th>Patient</th>
                <th>Notes</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {this.state.rooms.length ? (
                this.state.rooms.map(rooms => (
                  <tr key={rooms.id}>
                    <td style={{ width: "3rem" }}>{rooms.roomID}</td>
                    <td style={{ width: "8rem" }}>{patients.First_Name}</td>
                    <td style={{ width: "8rem" }}>{patients.Last_Name}</td>
                    <td style={{ width: "5rem" }}>{patients.Date_Of_Birth}</td>
                    <td style={{ width: "10rem" }}>{patients.Address}</td>
                    <td style={{ width: "3rem" }}>{patients.City}</td>
                    <td style={{ width: "2rem" }}>{patients.State}</td>
                    <td style={{ width: "3rem" }}>{patients.Zip_Code}</td>
                    <td style={{ width: "3rem" }}>{patients.Email}</td>
                    {/* <strong>
                                   {patients.First_Name} {patients.Last_Name} {patients.Address}  {patients.City}  {patients.State} {patients.Zip_Code} {patients.Email} {patients.orgID} {patients.RoomID}
                               </strong> */}
                    <td > <strong><DeleteBtn style={{ float: "left" }} onClick={() => { if (window.confirm('Are you sure you wish to delete this patient?')) this.deletePatient(patients.id) }} /></strong></td>
                  </tr>

                ))

              ) : (
                  <h3>No Results to Display</h3>
                )}
            </tbody>

          </Table>
          <div className="col-lg-5">
            <div className="row r1"></div>
            <div className="row" style={{ color: "Red", fontFamily: "TimesNewRoman", fontSize: "20px" }}>
              <div className="col-lg-12">
                <Form className="createRoomForm" onSubmit={this.handleSubmit}>
                  <Form.Group>
                    <Form.Label><strong>Enter Room Name</strong></Form.Label>
                    <Form.Control type="input" name="Name" onChange={this.handleInputChange} />
                  </Form.Group>
                  <Form.Group>
                    <Form.Label><strong>Enter Organization ID</strong></Form.Label>
                    <Form.Control type="input" name="orgID" onChange={this.handleInputChange} />
                  </Form.Group>
                  <Button variant="danger" type="submit" style={{ fontFamily: "TimesNewRoman" }}><strong>
                    Create Room!
            </strong>
                  </Button>
                </Form>
              </div>
            </div>
          </div>
        </div>
      </div>

    )
  };
}

//exports our Nav component
export default CreateRoomDiv;
