import React, { Component } from "react";
import styles from './sideBarMenu.css';
// import Glyphicon from 'react-bootstrap/lib/Glyphicon';
import { Nav, NavItem, Navbar, NavDropdown, DropdownItem } from 'react-bootstrap';
import { NavLink } from 'react-router-dom';
import { Form, Button } from 'react-bootstrap';


class CreateRoomDiv extends Component {

    constructor(props){
        super(props);
        this.state = { Name: '', orgID: '' };
      }

      handleChange = ({target}) => {
        this.setState({ [target.name]: target.value });
      };
      handleSubmit(event) {
        event.preventDefault();
        const data = new FormData(event.target);
        console.log
        fetch('/api/form-submit-url', {
          method: 'POST',
          body: data,
        });
      }

modalRef = React.createRef();

clickAddRoom = () => {
    console.log(this.modalRef)

    this.modalRef.current.setState({modalType: 'NewEventTable'}, () => {

      this.modalRef.current.toggleModal();
  });
    console.log(`you clicked the add event button`);
  };

  render() {
    return (
        <div className="mainDiv" >
            <div className="sideBarMenuContainer">
                <Navbar fluid="true" inverse="true" >
                    <Navbar.Collapse>
                        <Navbar.Text className="nt">
                            <NavLink className="nl" to="/Home">
                                Home
                        </NavLink>
                            <NavLink className="nl" to="/PatientList">
                                PatientList
                        </NavLink>
                            <NavLink className="nl" to="/CreateRoom">
                                CreateRoom
                        </NavLink>
                            <NavLink className="nl" to="/CompanyInfo">
                                Company Info
                        </NavLink>
                            <NavLink className="nl" to="/FrontDesk">
                                Front Desk
                        </NavLink>
                        </Navbar.Text>
                    </Navbar.Collapse>
                </Navbar>
            </div>

            <Form onSubmit={this.handleSubmit}>

        <label htmlFor="Name">Enter Room Name</label>
        <input id="Name" name="Name" type="text" /><br></br>

        <label htmlFor="orgID">Enter Organization ID </label>
        <input id="orgID" name="orgID" type="text" />

        <button>Create Room!</button>
      </Form>

<h3>Your room Name is: {this.state.Name}</h3>
<p>{ this.state.orgID }</p>
        </div>

    )
};
}
//exportes our Nav component
export default CreateRoomDiv;
