import React, { Component } from "react";
import { Form, Button, FormGroup } from 'react-bootstrap';
import "./style.css";


class AvailableRoom extends Component {
    constructor() {
        super();
        this.state = {
            patient: [],
            rooms: [],
            id: '',
            First_Name: '',
            Last_Name: '',
            roomID: '',
            Name: '',
            Available: '',
            patientID: ''
        };

        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleInputChange = event => {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        });
    };

    handleSubmit(event) {
        event.preventDefault();

        const data = new FormData(event.target);

        console.log(...data)


        console.log(this.state)


        fetch("http://localhost:3010/api/room/:id", {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                First_Name: this.state.First_Name,
                Last_Name: this.state.Last_Name,
                id: this.state.id,
                Available: 0
            })

        })
            .then(res => this.componentDidMount())
            .catch(err => console.log(err));
    }
    componentDidMount() {
        fetch("http://localhost:3010/api/room/", {
            method: 'GET',
            headers: { 'Content-Type': 'application/json' },


        }).then((res) => {
            res.json().then((data) => {
                this.setState({ rooms: data })
                console.log(this.state);
            });
        });

        fetch("http://localhost:3010/api/patient/", {
            method: 'GET',
            headers: { 'Content-Type': 'application/json' },


        }).then((res) => {
            res.json().then((data) => {
                this.setState({ patients: data, First_Name: "", Last_Name: "", Date_Of_Birth: "", Address: "", City: "", State: "", Zip_Code: "", Email: "", orgID: "", RoomID: "" })
            });
        });
    }
    render() {
        return (
            <Table className="patientTable" striped bordered hover>
                <thead>
                    <tr>
                        <th className="head">First Name</th>
                        <th className="head">Last Name</th>
                        <th className="head">RoomNo</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td></td>
                        <td></td>
                        <td><input type="text" name="room_number"></input></td>
                        <td><Button>Check-In</Button></td>
                        <td><Button>Check-Out</Button></td>
                    </tr>
                </tbody>
            </Table>
        )
    }
}
export default AvailableRoom
