import React , { Component } from "react";
import { Form, Button, FormGroup } from 'react-bootstrap';
import "./CompanyInfoForm.css";

class CompanyInfoForm extends Component {
  constructor() {
    super();
    this.state = {
      Name: '',
      Address: '',
      City: '',
      State: '', 
      Zip_Code: '', 
      Email: '', 
      Phone_Number: ''
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  handleSubmit(event) {
    event.preventDefault();

    const data = new FormData(event.target);

    console.log(...data)


    console.log(this.state)
    console.log(this.state.orgID)

    fetch("http://localhost:3010//api/organization/", {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        Name: this.state.Name,
        Address: this.state.Address,
        City: this.state.City,
        State: this.state.State, 
        Zip_Code: this.state.Zip_Code, 
        Email: this.state.Email, 
        Phone_Number: this.state.Phone_Number,


      })

    })

  }

  render() {
    return (
      <Form>
        <Form.Group controlId="Name">
          <Form.Label>Organization Name</Form.Label>
          <Form.Control type="input" />
        </Form.Group>
        <Form.Group controlId="Address">
          <Form.Label>Organization Address</Form.Label>
          <Form.Control type="input" />
        </Form.Group>
        <Form.Group controlId="City">
          <Form.Label>City</Form.Label>
          <Form.Control type="input" />
        </Form.Group>
        <Form.Group controlId="State">
          <Form.Label>State</Form.Label>
          <Form.Control type="input" />
        </Form.Group>
        <Form.Group controlId="Zip_Code">
          <Form.Label>Zip Code</Form.Label>
          <Form.Control type="input" />
        </Form.Group>
        <Form.Group controlId="Email">
          <Form.Label>Organization Contact Email</Form.Label>
          <Form.Control type="email" />
        </Form.Group>
        <Form.Group>
          <Form.Label controlId="Phone_Number" onChange={this.handleInputChange}>Organization Contact Phone Number</Form.Label>
          <Form.Control type="input" onChange={this.handleInputChange}/>
        </Form.Group>
        <Button variant="primary" type="submit">
          Update Info
  </Button>
      </Form>
    )
  }
}
  export default CompanyInfoForm
